mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT PROD_ID, PROD_NAME, PROD_SALE, PROD_COST, PROD_MILEAGE , LPROD_NM "lprod.lprodNm" , BUYER_NAME "buyer.buyerName" , ( SELECT COUNT(DISTINCT CART_MEMBER) FROM CART WHERE CART_PROD = PROD_ID ) MEM_COUNT
ORDER BY PROD_ID
) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:

    SELECT #{prodLgu} ||
	    LPAD(
	    NVL(TO_NUMBER(SUBSTR(MAX(PROD_ID), LENGTH(#{prodLgu})+1)),0)+1
	    ,6,'0')
	FROM PROD
	WHERE PROD_LGU=#{prodLgu}

