	, ATT_MIME
	, ATT_FILESIZE
	, ATT_FANCYSIZE
)VALUES(
	#{attNo,jdbcType=NUMERIC}
	, #{boNo,jdbcType=NUMERIC}
	, #{attFilename,jdbcType=VARCHAR}
	, #{attSavename,jdbcType=VARCHAR}
	, #{attMime,jdbcType=VARCHAR}
	, #{attFilesize,jdbcType=NUMERIC}
	, #{attFancysize,jdbcType=VARCHAR}
)
