mapper.insert:

	SELECT FREEBOARD_SEQ.NEXTVAL
	FROM DUAL

INSERT INTO FREEBOARD (
    BO_NO, BO_TITLE, BO_WRITER,
    BO_IP, BO_MAIL, BO_PASS,
    BO_CONTENT
) VALUES (
   #{boNo,jdbcType=NUMERIC}
	, #{boTitle,jdbcType=VARCHAR}
	, #{boWriter,jdbcType=VARCHAR}
	, #{boIp,jdbcType=VARCHAR}
	, #{boMail,jdbcType=VARCHAR}
	, #{boPass,jdbcType=VARCHAR}
	, #{boContent,jdbcType=CLOB}



)

mapper.select:
SELECT B.* FROM (SELECT ROWNUM RNUM, A.* FROM (SELECT a.BO_NO ABO_NO, BO_TITLE, BO_WRITER, BO_IP, BO_MAIL, BO_PASS, BO_CONTENT, BO_DATE, BO_HIT ,(SELECT DISTINCT COUNT(*) FROM ATTATCH B WHERE A.BO_NO=B.BO_NO) ATCH_COUNT FROM FREEBOARD A
ORDER BY BO_DATE DESC) A ) B
WHERE RNUM>=#{startRow} AND RNUM<=#{endRow}
